DECLARE @categoryCursor CURSOR;
SET @categoryCursor = CURSOR FOR SELECT [CategoryID] FROM [CMS_SettingsCategory] ORDER BY [CategoryLevel], [CategoryID]
DECLARE @categoryID int;
OPEN @categoryCursor
FETCH NEXT FROM @categoryCursor INTO @categoryID;
WHILE @@FETCH_STATUS = 0
BEGIN

UPDATE CMS_SettingsCategory SET CategoryChildCount = (SELECT COUNT(*) FROM CMS_SettingsCategory b WHERE b.CategoryParentID = a.CategoryID) FROM CMS_SettingsCategory a WHERE CategoryID = @categoryID

FETCH NEXT FROM @categoryCursor INTO @categoryID;
END

CLOSE @categoryCursor;
DEALLOCATE @categoryCursor;

DECLARE @NewPrefix VARCHAR(450);
DECLARE @OldLevel INT;
DECLARE @NewLevel INT;
SET @NewPrefix = (SELECT TOP 1 ##PATHCOLUMN## FROM ##TABLENAME## WHERE ##IDCOLUMN## = ##NEWPARENTID##); IF (@NewPrefix = '/')SET @NewPrefix = '';
SET @OldLevel = (SELECT TOP 1 ##LEVELCOLUMN## FROM ##TABLENAME## WHERE ##IDCOLUMN## = ##OLDPARENTID##);
SET @NewLevel = (SELECT TOP 1 ##LEVELCOLUMN## FROM ##TABLENAME## WHERE ##IDCOLUMN## = ##NEWPARENTID##);

-- Update level
IF @UpdateLevel = 1 UPDATE ##TABLENAME## SET ##LEVELCOLUMN## = ##LEVELCOLUMN## - @OldLevel + ISNULL(@NewLevel, -1) + 1 WHERE ##PATHCOLUMN## LIKE '##OLDPREFIX##%'

-- Update path
UPDATE ##TABLENAME## SET ##PATHCOLUMN## = ISNULL(@NewPrefix, '') + '/' + '##CURRENTITEM##' + SUBSTRING(##PATHCOLUMN##, LEN('##OLDPREFIX##'), LEN(##PATHCOLUMN##)) WHERE ##PATHCOLUMN## LIKE '##OLDPREFIX##%'

UPDATE CMS_WebPartLayout
SET WebPartLayoutIsDefault = NULL
WHERE WebPartLayoutWebPartID = @WebPartId;

-- Store max order so we keep the consistency of the orders (this matters when last item is being moved down) DECLARE @MaxOrder INT; DECLARE @CurrOrder INT; DECLARE @TargetOrder INT; SET @MaxOrder = (SELECT TOP 1 [##ORDERCOLUMN##] FROM [##TABLE##] WHERE ##WHERE## ORDER BY [##ORDERCOLUMN##] DESC); SET @CurrOrder = (SELECT TOP 1 [##ORDERCOLUMN##] FROM [##TABLE##] WHERE [##IDCOLUMN##] = @ObjectID);
-- Set the absolute order according to relativity setting IF @RelativeOrder = 1 BEGIN SET @TargetOrder = @CurrOrder + @NewOrder; END ELSE BEGIN SET @TargetOrder = @NewOrder; END
IF @CurrOrder > @TargetOrder BEGIN -- Moving from upper order to lower UPDATE [##TABLE##] SET [##ORDERCOLUMN##] = [##ORDERCOLUMN##] + 1 WHERE [##ORDERCOLUMN##] >= @TargetOrder AND [##ORDERCOLUMN##] < @CurrOrder AND ##WHERE## END ELSE BEGIN -- Moving from lower order to upper UPDATE [##TABLE##] SET [##ORDERCOLUMN##] = [##ORDERCOLUMN##] - 1 WHERE [##ORDERCOLUMN##] <= @TargetOrder AND [##ORDERCOLUMN##] > @CurrOrder AND ##WHERE## END -- Update the moved item, make sure the index is not bigger than maximal index SET @TargetOrder = CASE WHEN @MaxOrder < @TargetOrder THEN @MaxOrder Else @TargetOrder END SET @TargetOrder = CASE WHEN @TargetOrder < 1 THEN 1 Else @TargetOrder END UPDATE [##TABLE##] SET [##ORDERCOLUMN##] = @TargetOrder WHERE [##IDCOLUMN##] = @ObjectID SELECT @TargetOrder

/* Uncomment the following query after you configure Kentico CMS for full-text search as described in Developers Guide -> Installation and deployment -> Configuration of full-text search in files */
/* SELECT View_CMS_Tree_Joined.*, View_CMS_Tree_Joined.NodeName AS SearchResultName FROM CMS_Attachment INNER JOIN View_CMS_Tree_Joined ON View_CMS_Tree_Joined.DocumentID = CMS_Attachment.AttachmentDocumentID WHERE (##WHERE##) AND (([AttachmentName] Like N'%'+ @Expression + N'%') OR ([AttachmentTitle] Like N'%'+ @Expression + N'%') OR ([AttachmentDescription] Like N'%'+ @Expression + N'%')) OR (FREETEXT(AttachmentBinary, @expression)) ORDER BY ##ORDERBY## */
SELECT View_CMS_Tree_Joined.*, View_CMS_Tree_Joined.DocumentName AS SearchResultName FROM CMS_Attachment INNER JOIN View_CMS_Tree_Joined ON View_CMS_Tree_Joined.DocumentID = CMS_Attachment.AttachmentDocumentID WHERE (##WHERE##) AND (([AttachmentName] Like N'%'+ @Expression + N'%') OR ([AttachmentTitle] Like N'%'+ @Expression + N'%') OR ([AttachmentDescription] Like N'%'+ @Expression + N'%')) ORDER BY ##ORDERBY##
