Reporting_ReportGraph.GraphQuery:
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day');
SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'day');
SELECT '{'+'$analytics_codename.' + StatisticsCode + '$}' as Name,
SUM(HitsCount) as Count FROM Analytics_Statistics, Analytics_DayHits WHERE (StatisticsSiteID =
@CMSContextCurrentSiteID) AND ((StatisticsCode = @FirstCategory) OR (StatisticsCode = @SecondCategory))
 AND (StatisticsID = HitsStatisticsID) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
 GROUP BY StatisticsCode

SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'month');
SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'month');
SELECT '{'+'$analytics_codename.' + StatisticsCode + '$}' as Name,
SUM(HitsCount) as Count FROM Analytics_Statistics, Analytics_MonthHits WHERE (StatisticsSiteID =
@CMSContextCurrentSiteID) AND ((StatisticsCode = @FirstCategory) OR (StatisticsCode = @SecondCategory))
 AND (StatisticsID = HitsStatisticsID) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
 GROUP BY StatisticsCode

SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'year');
SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'year');
SELECT '{'+'$analytics_codename.' + StatisticsCode + '$}' as Name, 
SUM(HitsCount) as Count FROM Analytics_Statistics, Analytics_YearHits WHERE (StatisticsSiteID = 
@CMSContextCurrentSiteID) AND ((StatisticsCode = @FirstCategory) OR (StatisticsCode = @SecondCategory))
 AND (StatisticsID = HitsStatisticsID) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate) 
 GROUP BY StatisticsCode

SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'hour');
SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'hour');
SELECT '{'+'$analytics_codename.' + StatisticsCode + '$}' as Name,
SUM(HitsCount) as Count FROM Analytics_Statistics, Analytics_HourHits WHERE (StatisticsSiteID =
@CMSContextCurrentSiteID) AND ((StatisticsCode = @FirstCategory) OR (StatisticsCode = @SecondCategory))
 AND (StatisticsID = HitsStatisticsID) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
 GROUP BY StatisticsCode

EXEC Proc_Analytics_RemoveTempTable SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day');
CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits DECIMAL(15,2), Name NVARCHAR(300) COLLATE database_default );
-- SELECT INTO statement cannot be used, because it is not supported by Azure SQL INSERT INTO #AnalyticsTempTable SELECT [Date] AS StartTime, T1.Hits, StatisticsObjectCulture AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'day') AS Dates LEFT JOIN (SELECT HitsStartTime AS StartTime,SUM(HitsCount) AS hits,StatisticsObjectCulture FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode = @CodeName) GROUP BY HitsStartTime,StatisticsObjectCulture) AS T1 ON T1.StartTime = [Date]
EXEC Proc_Analytics_Pivot 'day'

