INSERT INTO #AnalyticsTempTable (StartTime,Hits,Name) SELECT [Date] AS StartTime ,T1.Hits,T1.CampaignDisplayName AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'day') AS Dates LEFT JOIN (SELECT HitsStartTime AS StartTime,SUM(HitsCount)AS hits,CampaignDisplayName FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID INNER JOIN Analytics_Campaign ON StatisticsObjectName = CampaignName WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode = @CodeName) AND (StatisticsSiteID = CampaignSiteID) AND (@CampaignName IS NULL OR @CampaignName IN ('',CampaignName)) GROUP BY HitsStartTime,CampaignDisplayName,CampaignName) AS T1 ON T1.StartTime = [Date]
EXEC Proc_Analytics_Pivot 'day' EXEC Proc_Analytics_RemoveTempTable

