EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits INT, Name NVARCHAR(300) );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'week'); INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name)
SELECT [Date] AS StartTime ,T1.Hits AS Hits ,T1.Name AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Week') AS Dates LEFT JOIN (SELECT HitsStartTime AS Interval, SUM(HitsCount) AS Hits ,ABVariantDisplayName AS Name FROM Analytics_Statistics INNER JOIN Analytics_WeekHits ON StatisticsID = Analytics_WeekHits.HitsStatisticsID INNER JOIN OM_ABVariant ON ABVariantName = SUBSTRING(StatisticsCode, CHARINDEX(';',StatisticsCode,14)+1,LEN (StatisticsCode)) AND ABVariantSiteID = @CMSContextCurrentSiteID WHERE StatisticsSiteID = @CMSContextCurrentSiteID AND StatisticsCode LIKE 'abconversion;%' AND (@TestName IS NULL OR @TestName = '' OR @TestName = SUBSTRING(StatisticsCode, 14, CHARINDEX(';',StatisticsCode,14)-14)) AND (@ConversionName IS NULL OR @ConversionName = '' OR @ConversionName = StatisticsObjectName) GROUP BY HitsStartTime,ABVariantDisplayName ) AS T1 ON Dates.Date=T1.Interval;
EXEC Proc_Analytics_Pivot 'week' EXEC Proc_Analytics_RemoveTempTable

EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits INT, Name NVARCHAR(300) );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day'); INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name)
SELECT [Date] AS StartTime ,T1.Hits AS Hits ,T1.Name AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Day') AS Dates LEFT JOIN (SELECT HitsStartTime AS Interval, SUM(HitsCount) AS Hits ,ABVariantDisplayName AS Name FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON StatisticsID = Analytics_DayHits.HitsStatisticsID INNER JOIN OM_ABVariant ON ABVariantName = SUBSTRING(StatisticsCode, CHARINDEX(';',StatisticsCode,14)+1,LEN (StatisticsCode)) AND ABVariantSiteID = @CMSContextCurrentSiteID WHERE StatisticsSiteID = @CMSContextCurrentSiteID AND StatisticsCode LIKE 'abconversion;%' AND (@TestName IS NULL OR @TestName = '' OR @TestName = SUBSTRING(StatisticsCode, 14, CHARINDEX(';',StatisticsCode,14)-14)) AND (@ConversionName IS NULL OR @ConversionName = '' OR @ConversionName = StatisticsObjectName) GROUP BY HitsStartTime,ABVariantDisplayName ) AS T1 ON Dates.Date=T1.Interval;
EXEC Proc_Analytics_Pivot 'day' EXEC Proc_Analytics_RemoveTempTable

EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits INT, Name NVARCHAR(300) );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'month'); INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name) SELECT [Date] AS StartTime ,T1.Hits AS Hits ,T1.Name AS Name
FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Month') AS Dates LEFT JOIN (SELECT HitsStartTime AS Interval, SUM(HitsValue) AS Hits ,ABVariantDisplayName AS Name FROM Analytics_Statistics INNER JOIN Analytics_MonthHits ON StatisticsID = Analytics_MonthHits.HitsStatisticsID INNER JOIN OM_ABVariant ON ABVariantName = SUBSTRING(StatisticsCode, CHARINDEX(';',StatisticsCode,14)+1,LEN (StatisticsCode)) AND ABVariantSiteID = @CMSContextCurrentSiteID WHERE StatisticsSiteID = @CMSContextCurrentSiteID AND StatisticsCode LIKE 'abconversion;%' AND (@TestName IS NULL OR @TestName = '' OR @TestName = SUBSTRING(StatisticsCode, 14, CHARINDEX(';',StatisticsCode,14)-14)) AND (@ConversionName IS NULL OR @ConversionName = '' OR @ConversionName = StatisticsObjectName) GROUP BY HitsStartTime,ABVariantDisplayName ) AS T1 ON Dates.Date=T1.Interval;
EXEC Proc_Analytics_Pivot 'month' EXEC Proc_Analytics_RemoveTempTable

EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits INT, Name NVARCHAR(300) );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'week'); INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name) SELECT [Date] AS StartTime ,T1.Hits AS Hits ,T1.Name AS Name
FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Week') AS Dates LEFT JOIN (SELECT HitsStartTime AS Interval, SUM(HitsValue) AS Hits ,ABVariantDisplayName AS Name FROM Analytics_Statistics INNER JOIN Analytics_WeekHits ON StatisticsID = Analytics_WeekHits.HitsStatisticsID INNER JOIN OM_ABVariant ON ABVariantName = SUBSTRING(StatisticsCode, CHARINDEX(';',StatisticsCode,14)+1,LEN (StatisticsCode)) AND ABVariantSiteID = @CMSContextCurrentSiteID WHERE StatisticsSiteID = @CMSContextCurrentSiteID AND StatisticsCode LIKE 'abconversion;%' AND (@TestName IS NULL OR @TestName = '' OR @TestName = SUBSTRING(StatisticsCode, 14, CHARINDEX(';',StatisticsCode,14)-14)) AND (@ConversionName IS NULL OR @ConversionName = '' OR @ConversionName = StatisticsObjectName) GROUP BY HitsStartTime,ABVariantDisplayName ) AS T1 ON Dates.Date=T1.Interval;
EXEC Proc_Analytics_Pivot 'week' EXEC Proc_Analytics_RemoveTempTable

EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits INT, Name NVARCHAR(300) );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day'); INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name) SELECT [Date] AS StartTime ,T1.Hits AS Hits ,T1.Name AS Name
FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Day') AS Dates LEFT JOIN (SELECT HitsStartTime AS Interval, SUM(HitsValue) AS Hits ,ABVariantDisplayName AS Name FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON StatisticsID = Analytics_DayHits.HitsStatisticsID INNER JOIN OM_ABVariant ON ABVariantName = SUBSTRING(StatisticsCode, CHARINDEX(';',StatisticsCode,14)+1,LEN (StatisticsCode)) AND ABVariantSiteID = @CMSContextCurrentSiteID WHERE StatisticsSiteID = @CMSContextCurrentSiteID AND StatisticsCode LIKE 'abconversion;%' AND (@TestName IS NULL OR @TestName = '' OR @TestName = SUBSTRING(StatisticsCode, 14, CHARINDEX(';',StatisticsCode,14)-14)) AND (@ConversionName IS NULL OR @ConversionName = '' OR @ConversionName = StatisticsObjectName) GROUP BY HitsStartTime,ABVariantDisplayName ) AS T1 ON Dates.Date=T1.Interval;
EXEC Proc_Analytics_Pivot 'day' EXEC Proc_Analytics_RemoveTempTable

EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits DECIMAL(10,1), Name NVARCHAR(300) COLLATE database_default );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'month');
INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name)
SELECT [Date] AS StartTime ,CAST (T1.Hits AS DECIMAL(10,2)) Hits ,T1.Name AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Month') AS Dates LEFT JOIN (SELECT HitsStartTime AS Interval, SUM(HitsCount) AS Hits ,ABVariantName AS Name FROM Analytics_Statistics INNER JOIN Analytics_MonthHits ON StatisticsID = Analytics_MonthHits.HitsStatisticsID INNER JOIN OM_ABVariant ON ABVariantName = SUBSTRING(StatisticsCode, CHARINDEX(';',StatisticsCode,14)+1,LEN (StatisticsCode)) AND ABVariantSiteID = @CMSContextCurrentSiteID WHERE StatisticsSiteID = @CMSContextCurrentSiteID AND StatisticsCode LIKE 'abconversion;%' AND (@TestName IS NULL OR @TestName = '' OR @TestName = SUBSTRING(StatisticsCode, 14, CHARINDEX(';',StatisticsCode,14)-14)) AND (@ConversionName IS NULL OR @ConversionName IN ('',StatisticsObjectName)) GROUP BY HitsStartTime,ABVariantName ) AS T1 ON Dates.Date=T1.Interval;
UPDATE #AnalyticsTempTable SET Hits = Hits/( SELECT CAST (SUM (HitsCount) AS DECIMAL (15,2)) FROM Analytics_Statistics JOIN Analytics_MonthHits ON Analytics_Statistics.StatisticsID = Analytics_MonthHits.HitsStatisticsID WHERE Analytics_Statistics.StatisticsCode = 'pageviews' AND Analytics_MonthHits.HitsStartTime = StartTime AND Analytics_Statistics.StatisticsObjectID IN (SELECT NodeID FROM View_CMS_Tree_Joined WHERE NodeAliasPath IN ( SELECT ABVariantPath From OM_ABVariant WHERE ABVariantName = Name AND ABVariantSiteID = @CMSContextCurrentSiteID AND ABVariantTestID = (SELECT ABTestID FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID) AND NodeSiteID = @CMSContextCurrentSiteID AND (DocumentCulture = StatisticsObjectCulture OR StatisticsObjectCulture IS NULL) /*culture */ AND (StatisticsObjectCulture IS NULL OR StatisticsObjectCulture = (SELECT ABTestCulture FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID) OR ((SELECT ABTestCulture FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID) IS NULL) ) ) ) ) * 100
UPDATE #AnalyticsTempTable SET Name = (SELECT ABVariantDisplayName FROM OM_ABVariant WHERE ABVariantName = Name AND ABVariantTestID IN (SELECT ABTestID FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID))
EXEC Proc_Analytics_Pivot 'Month' EXEC Proc_Analytics_RemoveTempTable

EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits DECIMAL(10,1), Name NVARCHAR(300) COLLATE database_default );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'week');
INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name)
SELECT [Date] AS StartTime ,CAST (T1.Hits AS DECIMAL(10,2)) Hits ,T1.Name AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Week') AS Dates LEFT JOIN (SELECT HitsStartTime AS Interval, SUM(HitsCount) AS Hits ,ABVariantName AS Name FROM Analytics_Statistics INNER JOIN Analytics_WeekHits ON StatisticsID = Analytics_WeekHits.HitsStatisticsID INNER JOIN OM_ABVariant ON ABVariantName = SUBSTRING(StatisticsCode, CHARINDEX(';',StatisticsCode,14)+1,LEN (StatisticsCode)) AND ABVariantSiteID = @CMSContextCurrentSiteID WHERE StatisticsSiteID = @CMSContextCurrentSiteID AND StatisticsCode LIKE 'abconversion;%' AND (@TestName IS NULL OR @TestName = '' OR @TestName = SUBSTRING(StatisticsCode, 14, CHARINDEX(';',StatisticsCode,14)-14)) AND (@ConversionName IS NULL OR @ConversionName IN ('',StatisticsObjectName)) GROUP BY HitsStartTime,ABVariantName ) AS T1 ON Dates.Date=T1.Interval;
UPDATE #AnalyticsTempTable SET Hits = Hits/( SELECT CAST (SUM (HitsCount) AS DECIMAL (15,2)) FROM Analytics_Statistics JOIN Analytics_WeekHits ON Analytics_Statistics.StatisticsID = Analytics_WeekHits.HitsStatisticsID WHERE Analytics_Statistics.StatisticsCode = 'pageviews' AND Analytics_WeekHits.HitsStartTime = StartTime AND Analytics_Statistics.StatisticsObjectID IN (SELECT NodeID FROM View_CMS_Tree_Joined WHERE NodeAliasPath IN ( SELECT ABVariantPath From OM_ABVariant WHERE ABVariantName = Name AND ABVariantSiteID = @CMSContextCurrentSiteID AND ABVariantTestID = (SELECT ABTestID FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID) AND NodeSiteID = @CMSContextCurrentSiteID AND (DocumentCulture = StatisticsObjectCulture OR StatisticsObjectCulture IS NULL) /*culture */ AND (StatisticsObjectCulture IS NULL OR StatisticsObjectCulture = (SELECT ABTestCulture FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID) OR ((SELECT ABTestCulture FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID) IS NULL) ) ) ) ) * 100
UPDATE #AnalyticsTempTable SET Name = (SELECT ABVariantDisplayName FROM OM_ABVariant WHERE ABVariantName = Name AND ABVariantTestID IN (SELECT ABTestID FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID))
EXEC Proc_Analytics_Pivot 'Week' EXEC Proc_Analytics_RemoveTempTable

