UPDATE #AnalyticsTempTable SET Hits = Hits/( SELECT CAST (SUM (HitsCount) AS DECIMAL (15,2)) FROM Analytics_Statistics JOIN Analytics_DayHits ON Analytics_Statistics.StatisticsID = Analytics_DayHits.HitsStatisticsID WHERE Analytics_Statistics.StatisticsCode = 'pageviews' AND Analytics_DayHits.HitsStartTime = StartTime AND Analytics_Statistics.StatisticsObjectID IN (SELECT NodeID FROM View_CMS_Tree_Joined WHERE NodeAliasPath IN ( SELECT ABVariantPath From OM_ABVariant WHERE ABVariantName = Name AND ABVariantSiteID = @CMSContextCurrentSiteID AND ABVariantTestID = (SELECT ABTestID FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID) AND NodeSiteID = @CMSContextCurrentSiteID AND (DocumentCulture = StatisticsObjectCulture OR StatisticsObjectCulture IS NULL) /*culture */ AND (StatisticsObjectCulture IS NULL OR StatisticsObjectCulture = (SELECT ABTestCulture FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID) OR ((SELECT ABTestCulture FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID) IS NULL) ) ) ) ) * 100
UPDATE #AnalyticsTempTable SET Name = (SELECT ABVariantDisplayName FROM OM_ABVariant WHERE ABVariantName = Name AND ABVariantTestID IN (SELECT ABTestID FROM OM_ABTest WHERE ABTestName = @TestName AND ABTestSiteID = @CMSContextCurrentSiteID))
EXEC Proc_Analytics_Pivot 'Day' EXEC Proc_Analytics_RemoveTempTable

SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'week');
SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'week');
SELECT '{'+'$analytics_codename.' + StatisticsCode + '$}' as Name,
SUM(HitsCount) as Count FROM Analytics_Statistics, Analytics_WeekHits WHERE (StatisticsSiteID =
@CMSContextCurrentSiteID) AND ((StatisticsCode = @FirstCategory) OR (StatisticsCode = @SecondCategory))
 AND (StatisticsID = HitsStatisticsID) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
 GROUP BY StatisticsCode

