INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name) SELECT [Date] AS StartTime ,T1.Hits,T1.Name AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'year') AS Dates LEFT JOIN (SELECT HitsStartTime AS StartTime,SUM(HitsCount)AS hits, CASE WHEN @ConversionName = '' THEN CampaignDisplayName ELSE ConversionDisplayName END AS Name FROM Analytics_Statistics INNER JOIN Analytics_YearHits ON Analytics_YearHits.HitsStatisticsID = Analytics_Statistics.StatisticsID INNER JOIN Analytics_Conversion ON StatisticsObjectName = ConversionName AND ConversionSiteID = StatisticsSiteID INNER JOIN Analytics_Campaign ON SUBSTRING(StatisticsCode, 16, LEN(StatisticsCode)) = @CampaignName AND CampaignName = @CampaignName AND CampaignSiteID = StatisticsSiteID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode LIKE @CodeName+';%') AND (@CampaignName IN ('',CampaignName) OR @CampaignName IS NULL) AND (@ConversionName IN ('',ConversionName) OR @ConversionName IS NULL)
GROUP BY HitsStartTime,CampaignName, CASE WHEN @ConversionName = '' THEN CampaignDisplayName ELSE ConversionDisplayName END ) AS T1 ON T1.StartTime = [Date]
EXEC Proc_Analytics_Pivot 'year' EXEC Proc_Analytics_RemoveTempTable

