INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name) SELECT [Date] AS StartTime ,T1.Hits,T1.Name AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'week') AS Dates LEFT JOIN (SELECT HitsStartTime AS StartTime,SUM(HitsCount) AS hits, CampaignDisplayName AS Name FROM Analytics_Statistics INNER JOIN Analytics_WeekHits ON Analytics_WeekHits.HitsStatisticsID = Analytics_Statistics.StatisticsID INNER JOIN Analytics_Campaign ON (StatisticsObjectName = CampaignName) AND StatisticsSiteID = CampaignSiteID
WHERE (StatisticsSiteID = @SiteID OR @SiteID = 0) AND (StatisticsCode LIKE @CodeName) GROUP BY HitsStartTime,CampaignName,CampaignDisplayName ) AS T1 ON T1.StartTime = [Date]
EXEC Proc_Analytics_Pivot 'week' EXEC Proc_Analytics_RemoveTempTable

