CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits DECIMAL(15,2), Name NVARCHAR(300) COLLATE database_default );
-- SELECT INTO statement cannot be used, because it is not supported by Azure SQL INSERT INTO #AnalyticsTempTable SELECT [Date] AS StartTime ,T1.Hits,T1.ConversionDisplayName AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'year') AS Dates LEFT JOIN (SELECT HitsStartTime AS StartTime,SUM(HitsValue)AS hits,ConversionDisplayName FROM Analytics_Statistics INNER JOIN Analytics_YearHits ON Analytics_YearHits.HitsStatisticsID = Analytics_Statistics.StatisticsID INNER JOIN Analytics_Conversion ON StatisticsObjectName = ConversionName WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode LIKE @CodeName+';%') AND SUBSTRING(StatisticsCode, 20, LEN(StatisticsCode)-19) = @CampaignName GROUP BY HitsStartTime,ConversionDisplayName) AS T1 ON T1.StartTime = [Date]
EXEC Proc_Analytics_Pivot 'year'

