EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits INT, Name NVARCHAR(300) COLLATE database_default );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'year');
INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name) SELECT [Date] AS StartTime ,T1.Hits AS Hits ,T1.Name AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Year') AS Dates LEFT JOIN ( SELECT HitsStartTime AS Interval, SUM(HitsCount) AS Hits ,OM_MVTest.MVTestDisplayName AS Name FROM Analytics_Statistics INNER JOIN Analytics_YearHits ON Analytics_Statistics.StatisticsID = Analytics_YearHits.HitsStatisticsID INNER JOIN OM_MVTest ON OM_MVTest.MVTestName = SUBSTRING(StatisticsCode, 15, CHARINDEX(';',StatisticsCode,15)-15) AND MVTestSiteID = StatisticsSiteID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID ) AND StatisticsCode LIKE 'mvtconversion;%' AND (@MVTestName IS NULL OR @MVTestName = '' OR @MVTestName = OM_MVTest.MVTestName) AND (@ConversionNAme IS NULL OR @ConversionName = '' OR @ConversionName = StatisticsObjectName) GROUP BY HitsStartTime,OM_MVTest.MVTestDisplayName ) AS T1 ON Dates.Date=T1.Interval;
EXEC Proc_Analytics_Pivot 'year' EXEC Proc_Analytics_RemoveTempTable

EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits INT, Name NVARCHAR(300) COLLATE database_default );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'year');
INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name)
SELECT [Date] AS StartTime ,T1.Hits AS Hits ,T1.Name AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Year') AS Dates LEFT JOIN ( SELECT HitsStartTime AS Interval, SUM(HitsCount) AS Hits , CASE WHEN MVTCombinationCustomName IS NULL OR MVTCombinationCustomName='' THEN MVTCombinationName ELSE MVTCombinationCustomName END AS Name
FROM Analytics_Statistics INNER JOIN Analytics_YearHits ON Analytics_Statistics.StatisticsID = Analytics_YearHits.HitsStatisticsID INNER JOIN OM_MVTCombination ON MVTCombinationPageTemplateID IN ( SELECT CASE WHEN [NodeTemplateForAllCultures] = 1 AND NodeTemplateID <> 0 THEN NodeTemplateID ELSE [DocumentPageTemplateID] END FROM View_CMS_Tree_Joined WHERE NodeSiteID = @CMSContextCurrentSiteID AND NodeAliasPath IN(SELECT MVTestPage FROM OM_MVTest WHERE MVTestName = @MVTestName AND MVTestSiteID = @CMSContextCurrentSiteID) AND DocumentCulture = StatisticsObjectCulture)
WHERE (StatisticsSiteID = @CMSContextCurrentSiteID ) AND StatisticsCode LIKE 'mvtconversion;%' AND MVTCombinationName = (SUBSTRING(StatisticsCode,LEN ('mvtconversion;'+@MVTestName+';')+1,LEN (StatisticsCode))) AND @MVTestName = SUBSTRING(StatisticsCode, 15, CHARINDEX(';',StatisticsCode,15)-15) AND (StatisticsObjectName = @ConversionName OR @ConversionName IS NULL OR @ConversionName ='') GROUP BY HitsStartTime,MVTCombinationName,MVTCombinationCustomName ) AS T1 ON Dates.Date=T1.Interval;
EXEC Proc_Analytics_Pivot 'year' EXEC Proc_Analytics_RemoveTempTable

