SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'year');
INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name) SELECT [Date] AS StartTime, CAST (SUM(HitsCount) AS DECIMAL(10,1)) AS Hits, Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'year') AS Dates LEFT JOIN (SELECT HitsCount,HitsStartTime, OM_MVTest.MVTestName AS Name FROM Analytics_YearHits JOIN Analytics_Statistics ON Analytics_Statistics.StatisticsID = Analytics_YearHits.HitsStatisticsID JOIN OM_MVTest ON SUBSTRING(StatisticsCode, 15, CHARINDEX(';',StatisticsCode,15)-15) = OM_MVTest.MVTestName WHERE Analytics_Statistics.StatisticsCode LIKE 'mvtconversion;%'AND StatisticsSiteID = @CMSContextCurrentSiteID AND (@MVTestName IS NULL OR @MVTestName =MVTestName OR @MVTestName ='') AND (@ConversionName = StatisticsObjectName OR @ConversionName = '' OR @ConversionName IS NULL) ) AS x ON x.HitsStartTime = Dates.Date GROUP BY [Date], Name
UPDATE #AnalyticsTempTable SET Hits = Hits/ ( SELECT CAST (SUM (HitsCount) AS DECIMAL (15,2)) FROM Analytics_Statistics JOIN Analytics_YearHits ON Analytics_Statistics.StatisticsID = Analytics_YearHits.HitsStatisticsID WHERE Analytics_Statistics.StatisticsCode = 'pageviews' AND Analytics_YearHits.HitsStartTime = StartTime AND Analytics_Statistics.StatisticsObjectID IN (SELECT NodeID FROM View_CMS_Tree_Joined WHERE NodeAliasPath IN (SELECT MVTestPage FROM OM_MVTest WHERE MVTestName = Name AND MVTestSiteID = @CMSContextCurrentSiteID) AND NodeSiteID = @CMSContextCurrentSiteID) AND (StatisticsObjectCulture IN (SELECT MVTestCulture FROM OM_MVTest WHERE MVTestName = Name AND MVTestSiteID = @CMSContextCurrentSiteID) OR (SELECT MVTestCulture FROM OM_MVTest WHERE MVTestName = Name AND MVTestSiteID = @CMSContextCurrentSiteID) IS NULL) )*100
UPDATE #AnalyticsTempTable SET Name = (SELECT MVTestDisplayName FROM OM_MVTest WHERE MVTestName = Name AND MVTestSiteID = @CMSContextCurrentSiteID)
EXEC Proc_Analytics_Pivot 'year' EXEC Proc_Analytics_RemoveTempTable

