IF (@ExternalID IS NOT NULL) AND (@ExternalID != N'') AND EXISTS(SELECT [InsightHitID] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Day] ON [SM_InsightHit_Day].[InsightHitInsightID] = [SM_Insight].[InsightID] WHERE ([InsightCodeName] = @CodeName) AND ([InsightPeriodType] = @PeriodType) AND ([InsightExternalID] = @ExternalID) AND ([InsightHitPeriodFrom] BETWEEN @FromDate AND @ToDate) ) BEGIN SELECT [Date] AS [StartTime], [T1].[Values] FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'day') AS [Dates] LEFT JOIN (SELECT [InsightHitPeriodFrom] AS [StartTime], [InsightHitValue] AS [Values] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Day] ON [SM_InsightHit_Day].[InsightHitInsightID] = [SM_Insight].[InsightID] WHERE ([InsightCodeName] = @CodeName) AND ([InsightPeriodType] = @PeriodType) AND (InsightExternalID = @ExternalID) ) AS [T1] ON [T1].[StartTime] = [Date] END

