IF (@ExternalID IS NOT NULL) AND (@ExternalID != N'') AND EXISTS(SELECT [InsightHitID] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Week] ON [SM_InsightHit_Week].[InsightHitInsightID] = [SM_Insight].[InsightID] WHERE ([InsightCodeName] = @CodeName) AND ([InsightPeriodType] = @PeriodType) AND ([InsightExternalID] = @ExternalID) AND ([InsightHitPeriodFrom] BETWEEN @FromDate AND @ToDate) ) BEGIN SELECT CONVERT (NVARCHAR(2),DATEPART(wk,[Date]))+'/'+CONVERT (NVARCHAR(4),DATEPART(YEAR,[Date])) AS [StartTime], [T1].[Values] FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Week') AS [Dates] LEFT JOIN (SELECT [InsightHitPeriodFrom] AS [StartTime], [InsightHitValue] AS [Values] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Week] ON [SM_InsightHit_Week].[InsightHitInsightID] = [SM_Insight].[InsightID] WHERE ([InsightCodeName] = @CodeName) AND ([InsightPeriodType] = @PeriodType) AND (InsightExternalID = @ExternalID) ) AS [T1] ON [T1].[StartTime] = [Date] END

